# A class loader to be used to load the implementation class
class _MoClassLoader(object):
    def __init__(self, fqModuleName):
        moduleName = fqModuleName.rsplit('.', 1)[1]
        self.modulePrefix = 'cobra.modelimpl.{0}'.format(moduleName)

    def __getattr__(self, className):
        try:
            moduleName = '{0}.{1}'.format(self.modulePrefix, className.lower())
            module = importlib.import_module(moduleName)
            return getattr(module, className)
        except Exception:
            raise AttributeError("'module' object has no attribute '{0}'".format(className))

